document.write("<style type=\"text/css\">   #mood{margin: 0 auto; position: relative;}  #mood ul li,#mood ul li div.pillar{display:inline-block;display:-moz-inline-stack;zoom:1;*display:inline;}  #mood ul li{vertical-align: bottom}  #mood ul #li{width:100%; padding-bottom:10px; text-align: center;}  #mood ul li label{ display:block;}  #mood ul li div.pillar{ height:20px; width:80px; background:url(https://www.eetop.cn/statics/images/mood/mood.gif) repeat-y 28px center; margin-bottom:5px;}  #mood ul li div.pass{background-position: -83px center;}  .mood1{height: 110px; }  .spp{    font-size: 16px;    font-weight: 500;    position: absolute;    top: 20px;  }  img.dz{    width: 30px;    margin-top: 40px;    margin-bottom: 5px;    cursor:pointer;  }  .fx{    margin-top: 45px;  }</style><div id=\"mood\" class=\"mood mood1\"><ul class=\"clearfix\">      <li id=\"li\" class=\"li1\">      <img id=\"dianzan\" onclick=\"vote(7)\" class=\"dz dz1\" src=\"../../../../statics/images/aa/dz2.jpg\"><span class=\"spp spp1\">0</span><br />    </li>  </ul><!-- 分享代码 --><div class=\"fr fx fx1\">  </div></div><script type=\"text/javascript\">function vote(id) {  var praise_img = $(\"#dianzan\");  $.getJSON(\'https://www.eetop.cn/index.php?m=mood&c=index&a=post&id=98-6962773-1&k=\'+id+\'&\'+Math.random()+\'&callback=?\', function(data){    if(data.status==1)  {      $(\'#mood\').html(data.data);      if(praise_img.attr(\"src\") == (\"../../../../statics/images/aa/dz1.jpg\")){        $(\"#dianzan\").attr(\"src\",\"../../../../statics/images/aa/dz2.jpg\");      }else {        $(\"#dianzan\").attr(\"src\",\"../../../../statics/images/aa/dz1.jpg\");      }    }else {      alert(data.data);      if(praise_img.attr(\"src\") == (\"../../../../statics/images/aa/dz2.jpg\")){        $(\"#dianzan\").attr(\"src\",\"../../../../statics/images/aa/dz1.jpg\");      }    }  })}</script>");