var autoSmartCrops = {};
sl.smartcrop = {
    "init": function() {
        var smartImages = $(".sl-smartcrop");
        $(smartImages).each(function(component){
            if($(this).prop("tagName").toLowerCase() == 'img' || $(this).prop("tagName").toLowerCase() == 'div'){
                var s7Path = $(this).data("scene7-path");
                autoSmartCrops = sl.smartcrop.getAutoSmartCrops(s7Path);
                var width = $(window).width();
                var optimalWidth = sl.smartcrop.getOptimalWidth(Object.keys(autoSmartCrops),width);
                var scImages= $(this).data("smartcrop-images");
                var imageSize;
                if(autoSmartCrops[optimalWidth]!=null){
                     imageSize = autoSmartCrops[optimalWidth].toLowerCase();
                     imageSize = imageSize.substring(1,imageSize.length);
                }
                //var imageCropPath =  s7Path+autoSmartCrops[optimalWidth]
                var imageCropPath =  sl.smartcrop.getResponsiveImage(imageSize,scImages);
                if($(this).hasClass("lazy-loading")){
                    $(this).attr("data-src",imageCropPath);
                }else{
                    if($(this).prop("tagName").toLowerCase() == 'img'){
                        $(this).attr("src",imageCropPath);
                    }
                    if($(this).prop("tagName").toLowerCase() == 'div'){
                        var style="background-image: url('"+imageCropPath+"')";
                        $(this).attr("style",style);
                    }
                }

            }
        });
    },


    "getResponsiveImage": function(id,cropImages){
        var src="", defaultsrc="";
        var images= cropImages.split(",");
        $(images).each(function(){
            var image = this.split("::");
            if(image[1]==id && image[1]!='default'){
                src =image[0];
            }else if(image[1]=='default') {
                defaultsrc = image[0];
            }
        });
        if(src==""){ src= defaultsrc;}
        return src;
    },

    /**
     * Get the width of an element or parent element if the width is smaller than the minimum width
     * @param {HTMLElement} component the image component
     * @param {HTMLElement | Node} parent the parent element
     * @returns {Number} the width of the element
     */
    "getWidth" : function(component, parent) {
        var width = component.offsetWidth;
        while (width < 20 && parent && !component._autoWidth) {
            width =  parent.offsetWidth;
            parent = parent.parentNode;
        }
        return width;
    },

    /**
     * Get the optimal width based on the available sizes
     * @param {[Number]} sizes the available sizes
     * @param {Number} width the element width
     * @returns {String} the optimal width
     */
    "getOptimalWidth" : function (sizes, width) {
        var len = sizes.length;
        var key = 0;
        while ((key < len - 1) && (sizes[key] < width)) {
            key++;
        }
        return sizes[key] !== undefined ? sizes[key].toString() : width;
    },

    "getAutoSmartCrops" : function(src) {
        var request = new XMLHttpRequest();
        if(src!="" && typeof(src)!="undefined"){
            var url = src + "?req=set,json";
            request.open("GET", url, false);
            request.onload = function() {
                if (request.status >= 200 && request.status < 400) {
                    // success status
                    var responseText = request.responseText;
                    var rePayload = new RegExp(/^(?:\/\*jsonp\*\/)?\s*([^()]+)\(([\s\S]+),\s*"[0-9]*"\);?$/gmi);
                    var rePayloadJSON = new RegExp(/^{[\s\S]*}$/gmi);
                    var resPayload = rePayload.exec(responseText);
                    var payload;
                    if (resPayload) {
                        var payloadStr = resPayload[2];
                        if (rePayloadJSON.test(payloadStr)) {
                            payload = JSON.parse(payloadStr);
                        }

                    }
                    // check "relation" - only in case of smartcrop preset
                    if (payload && payload.set.relation && payload.set.relation.length > 0) {
                        for (var i = 0; i < payload.set.relation.length; i++) {
                            autoSmartCrops[parseInt(payload.set.relation[i].userdata.SmartCropWidth)] =
                                ":" + payload.set.relation[i].userdata.SmartCropDef;
                        }
                    }
                } else {
                    // error status
                }
            };
            request.send();
        }
        return autoSmartCrops;
    }

};

