sl.components.quotecarousel = {
    "init": function() {
        var quotecarousel = this,
            $quotecarousel = $('.quote-carousel');

        $quotecarousel.each(function() {
            var $thisquotecarousel = $(this);
            var $quoteItems = $thisquotecarousel.find(".quote-carousel-item");
            var quotesSize = $quoteItems.length;
            var $quoteslickdot = $thisquotecarousel.find(".quote-carousel-slickdot-container"),
                $slickdot = $thisquotecarousel.find(".slickdot");

            var $thisquotecarouselbodysection=$thisquotecarousel.find('.quote-carousel-body-section');
            const totalItems=$thisquotecarousel.find('.quote-carousel-item').length;
            let startX=0;
            var currentIndex=0;

            $thisquotecarouselbodysection.on('touchstart',function(e){
                startX = e.originalEvent.touches[0].clientX;
            });

            $thisquotecarouselbodysection.on('touchmove', function (e) {
                const currentX = e.originalEvent.touches[0].clientX;
                const diffX = currentX - startX;
                $thisquotecarouselbodysection.css({
                    transition: 'none',
                });
            });

            $thisquotecarouselbodysection.on('touchend', function (e) {
                const endX = e.originalEvent.changedTouches[0].clientX;
                const diffX = endX - startX;

                $thisquotecarouselbodysection.css({
                    transition: 'all 1s ease-in-out',
                });

                if (diffX > 50 ) {
                    // Swipe right
                    currentIndex = (currentIndex === 0) ? totalItems - 1 : currentIndex - 1;
                } else if (diffX < -50 ) {
                    // Swipe left
                    currentIndex = (currentIndex === totalItems - 1) ? 0 : currentIndex + 1;
                }
                quotecarousel.quoteScroll($thisquotecarousel,quotesSize,"exact",currentIndex);
            });

            if(quotesSize==0){
                $($thisquotecarousel).hide();
            }else if(quotesSize==1){
                $quoteslickdot.hide();
            }

            // Paginate by clicking one of the dots
            $slickdot.on('click', function () {
                var slickdotitem = parseInt($(this).attr("item-index"),10);
                quotecarousel.quoteScroll($thisquotecarousel,quotesSize,"exact",slickdotitem);
            });
        })
    },
    'quoteScroll': function($el,quotesSize, scrollDirection,idx) {
        var currentActive = $el.find('.quote-carousel-item.active');
        var currentActiveDot = $el.find('.slickdot.active');
        if (scrollDirection == "exact"){
            newItemIndex = idx;
        } else {
            var currentItemIndex = $(currentActive).attr("item-index");
            var newItemIndex = parseInt(currentItemIndex,10);
            if (scrollDirection == "next") {
                newItemIndex = parseInt(currentItemIndex,10) + 1;
            } else {
                newItemIndex = parseInt(currentItemIndex,10) - 1;
            }
            if (newItemIndex >= quotesSize) {
                newItemIndex = 0;
            } else if (newItemIndex < 0) {
                newItemIndex = quotesSize - 1;
            }
        }
        $(currentActive).removeClass("active").addClass("inactive");
        $(currentActiveDot).removeClass("active").addClass("inactive");
        var newElement = $el.find(".quote-carousel-item[item-index='" + newItemIndex + "']");
        $(newElement).removeClass("inactive").addClass("active");
        var newDotElement = $el.find(".slickdot[item-index='" + newItemIndex + "']");
        $(newDotElement).removeClass("inactive").addClass("active");
    }
}

